  it 'returns the updated_at time' do
    expect(criteria_extractor.updated_at).to eq(Time.new(2021, 3, 5, 21, 11))
  end

  context 'when given a page containing vaccination criteria' do
    let(:expected_criteria) do
      [
        'you are aged 56 or over',
        'you are at high risk from coronavirus (clinically extremely vulnerable)',
        'you are an eligible frontline health or social care worker',
        'you have a condition that puts you at higher risk (clinically vulnerable)',
        'you have a learning disability',
        'you are a main carer for someone at high risk from coronavirus'
      ]
    end

    it 'returns the list of criteria' do
      expect(criteria_extractor.criteria).to match_array(expected_criteria)
    end
  end

